class Component{constructor(e){this.html,this.selector="#"+e,this.component}setComponent(){this.component=document.querySelector(this.selector)}create(){return this.html}activate(){this.component.classList.add("_active")}disactivate(){this.component.classList.remove("_active")}isActive(){return this.component.classList.contains("_active")}fixate(){this.component.classList.add("_fixed")}unfix(){this.component.classList.remove("_fixed")}}class Header extends Component{constructor(e){super(e),this.html='\n            <header class="header" id="header">\n                <div class="container">\n    \n                    <div class="header__inner">\n                        <div class="header__logo logo">\n                            <div class="logo__label">MT</div>\n                            <a href="#" class="logo__name">Myroslav Toloshnyi</a>\n                        </div>\n                    </div>        \n    \n                </div>\n            </header>\n        ',this.ANIMATION_DURATION=200}animate(e){this.component.animate(e,this.ANIMATION_DURATION)}}class Burger extends Component{constructor(e){super(e),this.html='\n            <div class="header__burger burger" id="burger">\n                <span></span>\n            </div>\n        '}}class Navigation extends Component{constructor(e){super(e),this.html='\n            <nav class="header__nav nav" id="nav">\n                <ul class="nav__wrapper">\n                    <li class="nav__item"><a href="#" class="nav__link">Main</a></li>\n                    <li class="nav__item"><a href="#" class="nav__link">Slots</a></li>\n                    <li class="nav__item"><a href="#" class="nav__link">Author</a></li>\n                    <li class="nav__item"><a href="#" class="nav__link">Blog</a></li>\n                </ul>\n            </nav>\n        '}}const headerObj=new Header("header"),burgerObj=new Burger("burger"),navigationObj=new Navigation("nav");function render(){document.addEventListener("DOMContentLoaded",()=>{document.querySelector("#page").insertAdjacentHTML("afterbegin",headerObj.create()),headerObj.setComponent();const e=document.querySelector(".header__inner");e.insertAdjacentHTML("beforeend",navigationObj.create()),navigationObj.setComponent(),navigationObj.isActive=function(){return navigation.classList.contains("_active")},e.insertAdjacentHTML("afterbegin",burgerObj.create()),burgerObj.setComponent(),burgerObj.isActive=function(){return burger.classList.contains("_active")},burgerhandler()})}function listenToScroll(){document.addEventListener("scroll",()=>{!function(){const e=document.querySelector(".preview");previewHeight=e.offsetHeight,scrolledFromTop=getBodyScrollTop(),headerObj.setComponent(),scrolledFromTop>previewHeight?(headerObj.fixate(),headerObj.animate({opacity:[0,1],transform:["translateY(-100px)","translateY(0)"]})):(headerObj.animate({opacity:[0,1],transform:["translateY(-100px)","translateY(0)"]}),headerObj.unfix())}()})}function burgerhandler(){const e=document.querySelector("body");burger.addEventListener("click",()=>{burgerObj.isActive()?(burgerObj.disactivate(),navigationObj.disactivate(),e.classList.remove("_fixed")):(burgerObj.activate(),navigationObj.activate(),e.classList.add("_fixed"))})}function getBodyScrollTop(){return self.pageYOffset||document.documentElement&&document.documentElement.scrollTop||document.body&&document.body.scrollTop}render(),listenToScroll();